import { createSelector } from "reselect";

const selectCart = (state) => {
  console.log("state", state);
  return state.cart;
};

export const selectCartItems = createSelector(
  [selectCart],
  (cart) => cart.items
);

export const selectCartItemsCount = createSelector(
  [selectCartItems],
  (cartItems) => {
    console.log(cartItems);
    return 20;
  }
);
